import React, { useEffect } from 'react';
import { actSearchApi } from './modules/actions';
import { connect } from 'react-redux';
import { actFilmNameapi } from '../ModulePhim/action';
import FileNameReducer from '../../components/ModulePhim/'

function SearchBar(props, props1) {

    useEffect(() => {
       props.fetchListRap();
       props1.fetchFilmName();
    }, []);

    const renderRap = () => {
        if (props.data && props.data.length > 0) {
            return props.data.map((item, index) => {
                return (
                    <option key={index}>{item.tenHeThongRap}</option>
                )
            })
        }
    }

    const renderPhim = () => {
        if(props1.dataFilm && props1.dataFilm.length > 0) {
            return props1.data.map((item, index) => {
                return (
                    <option key={index}>{item.tenPhim}</option>
                )
            })
        }
    }

    return (
        <div className="container search">
            <div className="search-item d-inline">
                <select className="dropdown">
                    <option defaultValue>Phim</option>
                    {/* <option>Ác Quỷ Đối Đầu - Deliver Us From Evil - (C18)</option>
                    <option>Tenet - (C16)</option>
                    <option>Ava (C18)</option>
                    <option>Turu: Gà Tây Mê Quẩy - Turu: The Wacky Hen - (P))</option>
                    <option>Dạ Quỷ Rừng Sâu - The Widow (C18)</option>
                    <option>Dị Nhân Bóng Tối - Transference: Dark Mutants (C18)</option>
                    <option>Ròm - (C18)</option>
                    <option>Gia Đình Chân To Phiêu Lưu Ký - Bigfoot Family (P)</option>
                    <option>Dinh Thự Xác Sống - Zombie Mansion</option>
                    <option>Vì Sao Đưa Bạn Tới - Jungle Beat - (P)</option>
                    <option>Bà Tôi Là Công Chúa - Oh! My Gran - (C16))</option>
                    <option>Mượn Xác - Unfamiliar - (C18)</option>
                    <option>Trò Chơi Của Quỷ - Await the Dawn (C18)</option>
                    <option>Xứ Sở Phép Thuật - The Magic Kids: Three Unlikely Heroes (P)</option>
                    <option>Greenland: Thảm Hoạ Thiên Thạch - Greenland - (C16)</option>
                    <option>Chiếc Ba Lô Đẫm Máu - Range Runners (C18)</option> */}
                    {renderPhim()}
                </select>
            </div>
            <div className="search-item d-inline">
                <select className="dropdown">
                    <option defaultValue>Rạp</option>
                    {renderRap()}
                </select>
            </div>
            <div className="search-item d-inline">
                <select className="dropdown">
                    <option defaultValue>Ngày xem</option>
                    <option>Hôm nay</option>
                    <option>Ngày mai</option>
                    <option>Thứ Hai</option>
                    <option>Thứ Ba</option>
                    <option>Thứ Tư</option>
                    <option>Thứ Năm</option>
                    <option>Thứ Sáu</option>
                    <option>Thứ Bảy</option>
                    <option>Chủ Nhật</option>
                </select>
            </div>
            <div className="search-item d-inline">
                <select className="dropdown">
                    <option defaultValue>Suất chiếu</option>
                    <option>18:40</option>
                    <option>19:40</option>
                    <option>20:40</option>
                    <option>21:40</option>
                    <option>22:40</option>
                    <option>23:40</option>
                    <option>24:40</option>
                </select>
            </div>
            <div className="search-item d-inline">
                <button>MUA VÉ NGAY</button>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        data: state.SearchReducer.data,
        dataFilm: state.FilmNameReducer.data,
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        fetchListRap: () => {
            dispatch(actSearchApi());
        },
        fetchFilmName: () => {
            dispatch(actFilmNameapi());
        }
    }
};



export default connect(mapStateToProps, mapDispatchToProps)(SearchBar);
