import * as ActionType from './constants';

let initialState = {
    loadingFilm: false,
    dataFilm: null,
    errFilm: null,
}

const filmNameReducer = (state = initialState, action) => {
    switch (action.type) {
        case ActionType.ACT_FILM_REQUEST:
            state.loading = true;
            state.dataFilm = null;
            state.err = null;
            return { ...state };
        case ActionType.ACT_FILM_SUCCESS:
            state.loading = false;
            state.dataFilm = action.payload;
            state.err = null;
            return { ...state };
        case ActionType.ACT_FILM_FAILED:
            state.loading = false;
            state.dataFilm = null;
            state.err = action.payload;
            return { ...state };
        default:
            return {...state};
    }
}

export default filmNameReducer;
